js_dict = {
    'fi': "const scrollAnimationClass = 'fi';const scrollAnimationShowClass = 'is_active';const triggerMarginDefault = 200;const scrollAnimationElm = document.querySelectorAll('.' + scrollAnimationClass);const fadeInScrollAnimationFunc = function () {const dataMargin = scrollAnimationClass + '_margin';const dataTrigger = scrollAnimationClass + '_trigger';const dataDelay = scrollAnimationClass + '_delay';for (let l = 0; l < scrollAnimationElm.length; l++) {let triggerMargin = triggerMarginDefault;const elm = scrollAnimationElm[l];let showPos = 0;if (elm.dataset[dataMargin] != null) {triggerMargin = parseInt(elm.dataset[dataMargin]);}if (elm.dataset[dataTrigger]) {showPos = document.querySelector(elm.dataset[dataTrigger]).getBoundingClientRect().top + triggerMargin;} else {showPos = elm.getBoundingClientRect().top + triggerMargin;}if (window.innerHeight > showPos) {const delay = (elm.dataset[dataDelay]) ? elm.dataset[dataDelay] : 0;setTimeout(function (index) {scrollAnimationElm[index].classList.add(scrollAnimationShowClass);}.bind(null, l), delay);}}};window.addEventListener('load', fadeInScrollAnimationFunc);window.addEventListener('scroll', fadeInScrollAnimationFunc);"}
